INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(22, 'listgroupadmins', 'List group admins', 'Liste der gruppen Administratoren', 'Ein Gruppenadministrator kann &uuml;ber die Symbole in der Spalte Aktion ausgew&auml;hlt oder gel&ouml;scht werden.\r\nEin neuer Gruppenadministrator kann durch klicken auf den Kopf mit dem Plus Zeichen angelegt werden.', 'A group administrator can be selected or deleted by clicking on the symbols in the Action column.\r\nNew group administrators can be added by clicking on the button with the plus sign at the bottom of the list.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(23, 'listprojects', 'Project administration', 'Projekte administrieren', 'Ein Projekt kann durch klicken auf die Symbole in der Spalte Aktion ausgew&auml;hlt oder gel&ouml;scht werden.\r\nNeue Projekte k&ouml;nnen durch klicken auf das Symbol mit dem Pluszeichen am Ende der Liste angelegt werden.', 'A project can be selected or deleted by clicking on the symbols in the Action column.\r\nNew projects can be added by clicking on the button with the plus sign at the bottom of the list.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(24, 'listrepos', 'Repository list', 'Repository Liste', 'Ein Repository kann durch Klicken auf die Symbole in der Spalte Aktion ausgew&auml;hlt oder gel&ouml;scht werden.\r\nEin neues Repository kann durch klicken auf den Knopf mit dem Plus Zeichen am Ende der Liste erstellt werden.', 'A repository can be selected or deleted by clicking on the symbols in the Action column.\r\nNew repositories can be added by clicking on the button with the plus sign at the bottom of the list.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(26, 'nopermission', 'nopermission', 'nopermission', '', '');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(27, 'repaccessrights', 'Report access rights', 'Bericht Zugriffsrechte', 'Der Bericht stellt die Repository Zugriffsrechte zu einem vorher ausgew&auml;hlten Datum dar. Um ein Datum auswzuw&auml;hlen bitte einfach in das Datumsfeld klicken.', 'The report shows the repository access rights valid at a selected date. To select a date simply click into the date field.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(28, 'repgranteduserrights', 'Bericht gew&auml;hrte Benutzerrechte', 'Report granted user rights', 'Der Bericht listet alle den einzelknen Benutzern gew&auml;hrten Rechte auf.', 'The report shows all rights granted to users.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(29, 'replockedusers', 'Report locked users', 'Bericht gesperrte Benutzer', 'Der Bericht listet alle gesperrten Benutzer auf.', 'The report shows all locked users.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(30, 'replog', 'Report log', 'Bericht Logfile', 'Der Bericht listet alle Eintr&auml;ge aus dem Log auf.', 'The report shows all entries from the log.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(31, 'repshowgroup', 'Report group', 'Bericht Gruppe', 'Es werden alle Details zu einer Gruppe angezeigt. Es werden alle Mitglieder der Gruppe, alle Gruppen Administratoren und alls mit der Gruppe verbundenen Repository Zugriffsrechte angezeigt.', 'All details about a group will be shown. All members of the group, all group administrators and all access rights granted to the group will be listed.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(32, 'repshowuser', 'Bericht Benutzer', 'Repoort user', 'Der Bericht zeigt alle Details zu einem Benutzer an. Es werden Gruppen alle Mitgliedschaften, alle Projekte f&uuml;r die der Benutzer verantwortlich ist und alle Repository Zugriffsrechte angezeigt.', 'The repost shows all details abaout an user. All group memberships, all projects the user is responsible for and all repository access rights are shown.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(33, 'searchresult', 'searchresult', 'searchresult', '', '');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(34, 'selectproject', 'Projekt ausw&auml;hlen', 'Select project', 'W&auml;hlen Sie das Projekt aus, f&uuml;r das Sie Rechte vergeben wollen.', 'Select the project you want to administer rights for.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(35, 'selectgroup', 'Select group', 'Gruppe ausw&auml;hlen', 'W&auml;heln Sie die Guppe aus, die Sie bearbeiten wollen.', 'Select the group you want to work with.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(36, 'setaccessright', 'Access rights', 'Zugriffsrechte', 'Als Zugriffsrecht stehen "None", "Read" und "Write" zur Verf&uuml;gung. "None" bedeutet, dass dem Benutzer der Zugriff auf die Datei oder das Verzeichnis und aller darunter liegenden Verzeichnisse verboten wird. Das Verbot kann durch das Erteilen der Rechte "Read" oder "Write" in einem untergeordneten Verzeichnis wieder aufgehoben werden. "Write" enth&auml;lt automatisch das Recht "Read".\r\n&Uuml;ber das Datum g&uuml;ltig von und g&uuml;tig bis kann fest gelegt werden, ab wann das Recht gilt und wann es automatisch wieder entzogen wird.\r\nDie erlaubten Benutzer und die erlaubten Gruppen geben an, wem das Recht erteilt wird.', 'You can choose between the access rights "None", "Read" and "Write". "None" means that the user may not access the file or the directory with all its subdirectories. The proscription can be removed from the subdirectories by granting the "Read" or "Write" permission. The right "Write" includes the right "Read" automatically.\r\nThe date fields valid from and valid until can be used to specify from when on the permission is valid and from when on the permission should be revoked automatically.\r\nThe users and groups allowed tell you whom the right is granted to.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(37, 'workonaccessright', 'Directory for access right', 'Verzeichnis f&uuml;r Zugriffsrecht', 'Der Subversion Modulpfad wird dem ausgew&auml;hlten Verzeichnis vorangestellt. Sie k&ouml;nnen das Verzeichnis aus der Verzeichnisliste ausw&auml;hlen und dort auch tiefer in den verzeichnisbaum einsteigen. ', 'The Subversion module path will prepend the chosen directory. You can browse the directory list and select a directory and can go deeper into the subdirectories.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(38, 'workongroup', 'Add or edit group', 'Gruppe hinzuf&uuml;gen oder &auml;ndern', 'Eine gruppe muss einen eindeutigen Namen, eine Beschreibung und mindestens ein Mitglied haben. wenn die gruppe keine Mitglieder mehr hat, muss sie gel&ouml;scht werden.', 'A group must have an unique name, a description and at least one member. If the group will have no more members, you have to delete the group.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(39, 'workongroupaccessright', 'workongroupaccessright', 'workongroupaccessright', 'W&auml;hlen Sie den Benutzer und das Recht, das dem Benutzer gew&auml;hrt werden soll aus.\r\nDas L&ouml;schen recht schlie&szlig;t das Recht editieren und lesen ein. Das Editieren Recht erlaubt das lesen und &auml;ndern der Gruppe. Das Leserecht erlaubt dem Benutzer nur, die Gruppe anzusehen.', 'Select the user ans the right you want to grant to the user.\r\nThe delete right includes edit and read permission, edit includes the permission to read and edit the group. Read permissions only allows the user to see the group.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(40, 'workonproject', 'Project administration', 'Projekte administrieren', 'Ein Subversion Projekt braucht einen eindeutigen Namen. Daneben muss der Modulpfad innerhalb des Repositories angegeben und das Repository ausgew&auml;hlt werden.Jedes Projket muss mindestens einen verantwortlichen Benutzer haben.', 'A subversion project needs an unique name. The path within the subversion repository must be entered and the repostirory must be selected too. Each project must have at least on responsible user.');
INSERT INTO `help` (`id`, `topic`, `headline_en`, `headline_de`, `helptext_de`, `helptext_en`) VALUES(41, 'workonrepo', 'Add or edit repository', 'Rebository hinzuf&uuml;gen oder &auml;ndern', 'Ein Repository ben&ouml;tigt einen eindeutigen Namen und einen Pfad. Der Pfad kann mit file:// beginnen, wenn der SVN Access Manager auf derselben Maschine l&auml;uft auf der auch die Repositories liegen. In dem Fall werden weder Benutzername noch Passwort f&uuml;r den Repository Zugriff ben&ouml;tigt.\r\nEin Repository kann direkt aus dem SVN Access Manager angelegt werden. Das funktioniert nur, wenn die Repositories auf demselben Server liegen auf dem auch der SVN Access Manager l&auml;uft. Wenn selinux verwendet wird, m&uuml;ssen es die selinux Regeln dem Webserver gestatten, Repositories anzulegen.\r\nBenutzen Sie ''/'' als Pfad Trennzeichen auf Windows Systemen. Sie m&uuml;ssen einen Repository Pfad z. B. wie folgt schreiben: file:///c:/svn/testrepo', 'A repository needs an unique name and a path. The path can start with file:// if SVN Access Manager is running on the same host as the repositories are located on. In this case no username and password are needed for the repository access. Otherwiese you will need the credentials to access the repository.\r\nA reposirory can be created directly from SVN Access Manager. This only works if SVN Access Manager is running on the same host as the repositories are located on and in case of selinux usage the selinux rules permit the webserver to create the repository.\r\nUse ''/'' as path separator on Windows systems. You must write down a repository path e. g. like this: file:///c:/svn/testrepo');