INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(1, 'login', 'Login', 'Login', 'Geben Sie Ihren Benutzernamen und Ihr Passwort ein. Sollte Ihr Passwort abgelaufen sein, m&uuml;ssen Sie es nach dem Login &auml;ndern.', 'Insert your username and your password. If you passwors is expired you have to change it after login.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(2, 'general', 'General', 'Allgemein', 'Sie k&ouml;nnen Ihren Vornamen, Ihren Namen und die Emailadresse &auml;ndern. Bitte beachten Sie, dass Sie eine g&uuml;ltige E-Mailadresse hinterlegen, da Sie sonst Informationen, Ihre Benutzerkennung betreffen, nicht bekommen. Das kann zur Sperre der Benutzerkennung f&uuml;hren.', 'You can change your given name, your name and your email address. Please keep in mind that you supply a valid email address because otherwise you can not receive informations concerning your account. This can lead to a lock out of your account.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(3, 'password', 'Password', 'Passwort', 'Geben Sie Ihr altes Passwort und dann zweimal das neue Passwort ein. Das neue Passwort muss den Passwort Richtlinien entsprechen.', 'Fill in your old password and the new password two times. The new password must fit the password policy.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(4, 'password_policy', 'Password policy', 'Passwort Richtlinien', 'Hinweise zu den Passwort Richtlinien.', 'Hints regarding the password policy.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(5, 'preferences', 'Preferences', 'Voreinstellungen', 'Hinterlegen Sie ihre bevorzugten Einstellungen.', 'Save your personnell preferences.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(6, 'main', 'Main menu', 'Hauptmen&uuml;', 'Das Hauptmen&uuml;. Hier sehen Sie alle Ihnen zur Verf&uuml;gung stehenden Funktionen.', 'The main menu. Here you can see all functions you can use.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(7, 'list_access_rights', 'Overview of access rights', '&Uuml;bersicht der Zugriffsrechte', 'Die Liste zeigt alle vergebenen Zugriffsrechte auf die verschiedenen Subversion Repositories. Es sind nur die Rechte zu sehen, die Sie auch administrieren k&ouml;nnen.\r\nSie k&ouml;nnen mehrere Rechte l&ouml;schen, indem Sie diese markieren und aus "ausgew&auml;hlte l&ouml;schen" klicken.', 'The list shows all available access rights to the different subversion repositories. You can only see the rights you can administer yourself.\r\nYou can delete a couple of rights by marking them and klick to "delete selected".');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(8, 'list_users', 'Users', 'Benutzer', 'Aus der Liste der Benutzer kann ein Benutzer &uuml;ber die Symbole rechts unter Aktion ausgew&auml;hlt oder gel&ouml;scht werden.\r\nNeue Benutzer werden &uuml;ber das Plus-Symbol am unteren Ende der Liste eingerichtet.', 'An user can be selected or deleted by clicking on the symbols in the Action column.\r\nNew users can be added by clicking on the button with the plus sign at the bottom of the list.');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(9, 'workonuser', 'User Administration / edit user', 'Benutzer Administration / Benutzer &auml;ndern', 'Ein Benutzer muss eine eindeutige Benutzerkennung haben. Diese kann nur bei der Neuanlage vergeben werden und kann dann nicht mehr ge&auml;ndert werden.\r\nBei der Neuanlage muss dem Benutzer ein Passwort entsprechend der Passwort Richtlinie zugewiesen werden.\r\nDie Emailadresse muss immer angegeben werden. Es muss sich um eine g&uuml;ltige E-Mailadresse handeln. Diese Adresse ist wichtig, da Benachrichtigugen zu abgelaufenen Passworten an diese Adresse geschickt werden.\r\nIm Bereich "Globale Benutzer Rechte" k&ouml;nnen &uuml;bergreifende Rechte vergeben werden. Dabei schlie&szlig;t das Recht "L&ouml;schen" die rechte "editieren" und "lesen" mit ein und das Recht "editieren" schlie&szlig;t das Recht "lesen" mit ein.', 'An user must have an unique username. This username can only be set during initial generation and connot be changed afterwards. At generation of the user a password corresponding to the password policy must be assigned.\r\nThe email address must always be set. It must be a valid email address. The address is important because notifications regarding expired passwords are sent to this address.\r\nIn the section "Global user rights" global rights can be assigned to the user. The right "delete" includes the rights "edit" and "read" and the right "edit" includes the right "read".');
INSERT INTO help (id, topic, headline_en, headline_de, helptext_de, helptext_en) VALUES(10, 'list_groups', 'Group administration', 'Gruppen Administration', 'Aus der Liste der Gruppen kann eine Gruppe &uuml;ber die Symbole rechts unter Aktion ausgew&auml;hlt oder gel&ouml;scht werden.\r\nNeue Gruppen werden &uuml;ber das Plus-Symbol am unteren Ende der Liste eingerichtet.', 'A group can be selected or deleted by clicking on the symbols in the Action column.\r\nNew groups can be added by clicking on the button with the plus sign at the bottom of the list.');